// SavingsCalculator.js

import React, { useState } from 'react';
import './SavingsCalculator.css'; // Import custom styles for the SavingsCalculator page

const SavingsCalculator = () => {
  const [formData, setFormData] = useState({
    currentBill: '',
    unitsConsumed: '',
    state: '',
    solarSystemSize: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Calculate savings here based on the input data
    // Display the calculated savings to the user
    console.log(formData);
  };

  return (
    <div className="savings-calculator-container">
      <h2>Calculate Your Savings</h2>
      <form className="savings-calculator-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="currentBill">Current Electricity Bill:</label>
          <input
            type="number"
            id="currentBill"
            name="currentBill"
            value={formData.currentBill}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="unitsConsumed">Number of Units Consumed:</label>
          <input
            type="number"
            id="unitsConsumed"
            name="unitsConsumed"
            value={formData.unitsConsumed}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="state">State:</label>
          <input
            type="text"
            id="state"
            name="state"
            value={formData.state}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="solarSystemSize">Solar System Size (kW):</label>
          <input
            type="number"
            id="solarSystemSize"
            name="solarSystemSize"
            value={formData.solarSystemSize}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="calculate-button">Calculate</button>
      </form>
    </div>
  );
}

export default SavingsCalculator;
